sudo apt install zip unzip jq -y

cd /etc/systemd/system && printf "[Unit]\nDescription=Rise_Selfbot\nAfter=syslog.target\nAfter=network.target\n\n[Service]\nType=forking\nUser=root\nWorkingDirectory=/home/Rise_Linux\nRestart=always\nExecStart=/usr/bin/screen -L -dmS Rise_Selfbot ./Rise_Selfbot\n\n[Install]\nWantedBy=multi-user.target" > ./riseLinux.service && cd /home && wget https://riseselfbot.xyz/we/Rise_Linux.zip && unzip Rise_Linux.zip && chmod -R 777 /home/Rise_Linux && rm Rise_Linux.zip && cd /home/Rise_Linux && echo Please Enter Your License: && read key && jq --arg variable "$key" '.license = $variable' license.json > "tmp" && mv "tmp" license.json && echo Please Enter Your Token: && read tkn && jq --arg variable "$tkn" '.token = $variable' config.json > "tmp" && mv "tmp" config.json && jq '.skip_menu = true' config.json > "tmp" && mv "tmp" config.json && systemctl enable riseLinux && service riseLinux start && clear && echo Rise Is Setup, Enjoy! && echo To finish setup system will now restart! && reboot